(function(){tinymce.PluginManager.requireLangPack('markettoimages');tinymce.create('tinymce.plugins.MarkettoImagesPlugin',{init:function(ed,url){ed.addCommand('markettoImages',function(){ed.windowManager.open({file:url+'/dialog.htm',width:320+parseInt(ed.getLang('markettoimages.delta_width',0)),height:170+parseInt(ed.getLang('markettoimages.delta_height',0)),inline:1},{plugin_url:url,some_custom_arg:'custom arg'})});ed.addButton('markettoimages',{title:'markettoimages.desc',cmd:'markettoImages',image:url+'/img/markettoimages.gif'});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('markettoimages',n.nodeName=='IMG')})},createControl:function(n,cm){return null},getInfo:function(){return{longname:'Marketto.ru Images Plugin',author:'Viktor Kuzhelny',authorurl:'http://marketto.ru/',infourl:'http://marketto.ru/page/tinymce-images-plugin',version:"1.0"}}});tinymce.PluginManager.add('markettoimages',tinymce.plugins.MarkettoImagesPlugin)})();